import './App.css';
import { todos, newTodos } from './TodoData';

import React, { useState, useEffect } from 'react';
import { Counter } from './Counter';
function RandomTodos() {
  const [usetodo, setUsetodo] = useState(todos);
  const [timeA, setTimeA] = useState(0);

  function addRandomTodo() {
    const randomIndex = Math.floor(Math.random() * newTodos.length);
    let randomID = usetodo[usetodo.length - 1].id + 1;
    const randomTodo = { id: randomID, ...newTodos[randomIndex] };
    return setUsetodo((todosList) => [...todosList, randomTodo]);
  }
  const showtodo = usetodo.map((todos) => (
    <TodoItem text={todos.description} id={todos.id} key={todos.id}></TodoItem>
  ));
  console.log(showtodo);
  function TodoItem(props) {
    // console.log(props.id);
    return (
      <li>
        <label htmlFor="checkbox">
          {props.text}
          <input type="checkbox" onSelect={() => setUsetodo(props.id)}></input>
        </label>
        <button htmlFor="checkbox" onClick={() => deleteToDo(props.id)}>
          delete
        </button>
      </li>
    );
  }
  function deleteToDo(id) {
    setUsetodo((prevTodos) => {
      return prevTodos.filter((todo) => todo.id !== id);
    });
  }
  return (
    <div>
      <button onClick={addRandomTodo}> add todo </button>
      {showtodo}
    </div>
  );
}
function App() {
  return (
    <div className="App">
      <p>
        <Counter />
      </p>
      <h1>ToDo List</h1>
      <RandomTodos />
    </div>
  );
}
export default App;
