import './App.css';
import { todos, newTodos } from './todoData';
import { useState } from 'react';

function AddRandomTodos() {
  const [usetodos, setUsetodo] = useState(todos);

  const randomIndex = Math.floor(Math.random() * newTodos.length);
  let randomID = usetodos[usetodos.length - 1].id + 1;
  const randomTodo = { id: randomID, ...newTodos[randomIndex] };
  const updatedTodo = setUsetodo((todosList) => [...todosList, randomTodo]);
  return updatedTodo;

  function TodoList() {
    const [usetodos, setUsetodo] = useState(todos);
    return usetodos.map((todo) => {
      return (
        <div>
          <ul>
            <TodoItem
              text={todo.description}
              id={todo.id}
              key={todo.id}
            ></TodoItem>
          </ul>
        </div>
      );
    });
  }

  function TodoItem(props) {
    const [usetodos, setUsetodo] = useState(todos);
    console.log(usetodos);

    return (
      <li>
        <label htmlFor="checkbox">
          {props.text}
          <input type="checkbox" onSelect={() => setUsetodo(props.id)}></input>
          <button htmlFor="checkbox" onClick={() => DeleteToDo(props.id)}>
            delete
          </button>
        </label>
      </li>
    );
  }
}
function App() {
  return (
    <div className="App">
      {updatedTodo}
      <button onClick={AddRandomTodos}> add todo</button>
    </div>
  );
}

export default App;
