import './App.css';
import { Counter } from './components/Counter';
// import RandomTodos from './components/RandomTodos';
import { todos, newTodos } from './components/TodoData';
import React, { useState } from 'react';

function TodosMethods() {
  const [usetodo, setUsetodo] = useState(todos);

  //todoList
  const TodoList = usetodo.map((todos) => (
    <TodoItem
      text={todos.description}
      checked={todos.checked}
      id={todos.id}
      key={todos.id}
    ></TodoItem>
  ));
  console.log(TodoList);

  //todoItem
  function TodoItem({ text, checked }) {
    return (
      <li>
        {text};
        {/* <span>  className={checked ? ‘checked’ : ‘unchecked’}</span> */}
        {/* <input type={‘checkbox’} value={checked} onChange={onCheck}></input>  */}
        <input type="checkbox" onChange={() => decorateToDo(props.id)} />
        <button onClick={() => deleteToDo(props.id)}>delete</button>
      </li>
    );
  }

  //add RandomTODO
  function addRandomTodo() {
    let randomID;
    if (usetodo.length === 0) {
      randomID = 1;
    } else {
      randomID = usetodo[usetodo.length - 1].id + 1;
    }
    const randomTodo = {
      id: randomID,
      ...newTodos[Math.floor(Math.random() * newTodos.length)],
    };
    return setUsetodo((todosList) => [...todosList, randomTodo]);
  }

  //deleteTODO
  function deleteToDo(id) {
    console.log('hiii');
    console.log(TodoList); //??
    if (TodoList.length === 0) {
      // why not??
      return <h1>no item to delete</h1>;
    } else {
      setUsetodo((prevTodos) => {
        return prevTodos.filter((todo) => todo.id !== id);
      });
    }
  }

  //strike ThroughTODO
  function decorateToDo(id) {
    var strikeThroughList = [...usetodo];
    strikeThroughList.forEach((item) => {
      if (item.id === id) {
        item.checked = !item.checked;
      }
    });
    setUsetodo(strikeThroughList);
  }

  return (
    <div>
      <button onClick={addRandomTodo}> add todo </button>
      {TodoList}
    </div>
  );
}

function App() {
  return (
    <div className="App">
      <Counter />
      <h1>ToDo List</h1>
      <TodosMethods />
    </div>
  );
}
export default App;
