import { todos, newTodos } from './components/TodoData';

import React, { useState, useEffect } from 'react';
import { Counter } from './components/Counter';

function RandomTodos() {
  const [usetodo, setUsetodo] = useState(todos);

  function addRandomTodo() {
    let randomID;
    if (usetodo.length === 0) {
      randomID = 1;
    } else {
      randomID = usetodo[usetodo.length - 1].id + 1;
    }
    const randomTodo = {
      id: randomID,
      ...newTodos[Math.floor(Math.random() * newTodos.length)],
    };
    return setUsetodo((todosList) => [...todosList, randomTodo]);
  }
  const TodoList = usetodo.map((todos) => (
    <TodoItem text={todos.description} id={todos.id} key={todos.id}></TodoItem>
  ));
  console.log(TodoList);
  function TodoItem(props) {
    return (
      <li>
        <label htmlFor="checkbox">
          {props.text}
          <input
            type="checkbox"
            onChange={() => decorateToDo(props.id)}
          ></input>
        </label>
        <button htmlFor="checkbox" onClick={() => deleteToDo(props.id)}>
          delete
        </button>
      </li>
    );
  }
  function deleteToDo(id) {
    console.log('hiii');
    console.log(TodoList); //??
    if (TodoList.length < 1) {
      // why not??
      return <h1>no itm</h1>;
    } else {
      setUsetodo((prevTodos) => {
        return prevTodos.filter((todo) => todo.id !== id);
      });
    }
  }

  function decorateToDo(id) {
    setUsetodo((prevTodos) => {
      const decorate = prevTodos.filter((todo) => {
        console.log(todo.description);
        // <li className="todoList">todo.description</li>;
        return <li className="todoList">todo.id !== id</li>;
      });
      return decorate;
    });
  }

  return (
    <div>
      <button onClick={addRandomTodo}> add todo </button>
      {TodoList}
    </div>
  );
}

export default RandomTodos;
