import '../App.css';
export function TodoItem({ description, checked, id, markTodo, deleteTodo }) {
  function deleteitem() {
    deleteTodo(id);
  }
  return (
    <div>
      <li>
        <span className={checked ? 'checked' : 'unchecked'}>
          {description}
          <input type="checkbox" onChange={() => markTodo(id)}></input>
        </span>
        <button onClick={deleteitem}>Delete</button>
      </li>
    </div>
  );
}
