import { TodosArray, NewTodosArray } from './TodoData';
import { useState } from 'react';
function AddRandomTodo() {
  const [useTodo1, setUseTodo1] = useState(TodosArray);
  let randomID;
  if (useTodo1.length === 0) {
    randomID = 1;
  } else {
    randomID = useTodo1[useTodo1.length - 1].id + 1;
  }
  const randomTodo = {
    id: randomID,
    ...NewTodosArray[Math.floor(Math.random() * NewTodosArray.length)],
  };
  const AddedListWithRandomTodo = setUseTodo1((todosList) => [
    ...todosList,
    randomTodo,
  ]);
  return AddedListWithRandomTodo;
}

export default AddRandomTodo;
