import './App.css';
import { Counter } from './components/Counter';
// import RandomTodos from './components/RandomTodos';
// import { TodosArray, NewTodosArray } from './components/TodoData';
import React, { useState } from 'react';

// css
// .checked {
//   text-decoration: line-through;
//   color: red;
// }

const TodosArray = [
  {
    id: 1,
    description: 'Get out of bed',
    checked: false,
  },
  {
    id: 2,
    description: 'Brush teeth',
    checked: false,
  },
  {
    id: 3,
    description: 'Eat breakfast',
    checked: false,
  },
];

const NewTodosArray = [
  {
    description: 'got to bed',
  },
  {
    description: 'dream well',
  },
  {
    description: 'explain the dream in the morning',
  },
];

function TodosMethods() {
  const [useTodo, setUseTodo] = useState(TodosArray);

  //TodoList
  const TodoList = useTodo.map((todos) => (
    <TodoItem
      text={todos.description}
      checked={todos.checked}
      id={todos.id}
      key={todos.id}
    />
  ));
  console.log(TodoList);

  //TodoItems
  function TodoItem({ text, checked, id }) {
    return (
      <li>
        <span className={checked ? 'checked' : 'unchecked'}> {text}</span>
        <input
          type={'checkbox'}
          value={checked}
          onChange={() => MarkTodo(id)}
        ></input>
        <button onClick={() => DeleteTodo(id)}>Delete</button>
      </li>
    );
  }

  //add a randomTodo
  function AddRandomTodo() {
    let randomID;
    if (useTodo.length === 0) {
      randomID = 1;
    } else {
      randomID = useTodo[useTodo.length - 1].id + 1;
    }
    const randomTodo = {
      id: randomID,
      ...NewTodosArray[Math.floor(Math.random() * NewTodosArray.length)],
    };
    const AddedListWithRandomTodo = setUseTodo((todosList) => [
      ...todosList,
      randomTodo,
    ]);
    return AddedListWithRandomTodo;
  }

  //Delete a Todo
  function DeleteTodo(id) {
    setUseTodo((prevTodos) => {
      const ListAfterDelete = prevTodos.filter((todo) => todo.id !== id);
      console.log(ListAfterDelete);
      // if (ListAfterDelete.length === 0) {
      //   setUseTodo('no more items');
      // }
      return ListAfterDelete;
    });
  }

  //Strike through Todos
  function MarkTodo(id) {
    var strikeThroughList = [...useTodo];
    strikeThroughList.forEach((item) => {
      if (item.id === id) {
        item.checked = !item.checked;
      }
    });
    setUseTodo(strikeThroughList);
  }

  return (
    <div>
      <button onClick={AddRandomTodo}> Add Todo </button>
      {TodoList}
    </div>
  );
}

function App() {
  return (
    <div className="App">
      <Counter />
      <h1>ToDo List</h1>
      <TodosMethods />
    </div>
  );
}
export default App;
