import './App.css';

import React, { useState } from 'react';
import { todos, newTodos } from './TodoData';
import { Counter } from './Counter';

function RandomTodos() {
  const [usetodo, setUsetodo] = useState(todos);
  console.log(usetodo);

  function addRandomTodo() {
    const randomIndex = Math.floor(Math.random() * newTodos.length);
    let randomID = usetodo[usetodo.length - 1].id + 1;
    const randomTodo = { id: randomID, ...newTodos[randomIndex] };

    console.log(randomTodo);
    return setUsetodo((todosList) => [...todosList, randomTodo]);
  }

  const TodoList = usetodo.map((todos) => (
    <TodoItem text={todos.description} key={todos.id} id={todos.id}></TodoItem>
  ));
  function TodoItem({ text, id }) {
    return (
      <li>
        {text}

        <label htmlFor="checkbox">
          {/* {props.text} */}
          <input type="checkbox" onSelect={() => setUsetodo({ id })}></input>
          <button htmlFor="checkbox" onClick={() => DeleteToDo({ id })}>
            delete
          </button>
        </label>
      </li>
    );
  }

  function DeleteToDo(id) {
    const [usetodos, setUsetodo] = useState(todos);
    setUsetodo((prevTodos) => {
      return prevTodos.filter((todo) => todo.id !== id);
    });
  }

  return (
    <div>
      {TodoList}
      <button onClick={addRandomTodo}> add todo </button>
    </div>
  );
}

function App() {
  return (
    <div className="App">
      <Counter />
      <h1>Todos</h1>
      <RandomTodos />
    </div>
  );
}

export default App;
